const http = require("https");

const options = {
  "method": "POST",
  "hostname": "example.com",
  "port": null,
  "path": "/",
  "headers": {
    "content-type": "text/plain"
  }
};

const req = http.request(options, function (res) {
  const chunks = [];

  res.on("data", function (chunk) {
    chunks.push(chunk);
  });

  res.on("end", function () {
    const body = Buffer.concat(chunks);
    console.log(body.toString());
  });
});

req.write("Hi, there");
req.end();

